	const char* HolesCore::getCompiledHash()
	{
		return CommHoleLocalizerIDL::REPO_HASH;
	}
	
	void HolesCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
		// get hash value(s) for CommHoleLocalizer::BoundingBox(idl_Holes.list)
		CommHoleLocalizer::BoundingBox::getAllHashValues(hashes);
	}
	
	void HolesCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "HolesCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommHoleLocalizer::BoundingBox(idl_Holes.list)
		CommHoleLocalizer::BoundingBox::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t HolesCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		std::vector<CommHoleLocalizerIDL::BoundingBox>::const_iterator data_listIt;
		for(data_listIt=data.list.begin(); data_listIt!=data.list.end(); data_listIt++) {
			seed += CommHoleLocalizer::BoundingBox::generateDataHash(*data_listIt);
		}
		
		return seed;
	}
	#endif
	
