	void HolesCore::to_ostream(std::ostream &os) const
	{
	  os << "Holes(";
	  std::vector<CommHoleLocalizer::BoundingBox>::const_iterator listIt;
	  std::vector<CommHoleLocalizer::BoundingBox> listList = getListCopy();
	  for(listIt=listList.begin(); listIt!=listList.end(); listIt++) {
	  	listIt->to_ostream(os);
	  }
	  os << ") ";
	}
	
	// convert to xml stream
	void HolesCore::to_xml(std::ostream &os, const std::string &indent) const {
		size_t counter = 0;
		
		std::vector<CommHoleLocalizer::BoundingBox>::const_iterator listIt;
		std::vector<CommHoleLocalizer::BoundingBox> listList = getListCopy();
		counter = 0;
		os << indent << "<listList n=\"" << listList.size() << "\">";
		for(listIt=listList.begin(); listIt!=listList.end(); listIt++) {
			os << indent << "<list i=\"" << counter++ << "\">";
			listIt->to_xml(os, indent);
			os << indent << "</list>";
		}
		os << indent << "</listList>";
	}
	
	// restore from xml stream
	void HolesCore::from_xml(std::istream &is) {
		size_t counter = 0;
		
		static const Smart::KnuthMorrisPratt kmp_listList("<listList n=\"");
		static const Smart::KnuthMorrisPratt kmp_list("\">");
		
		if(kmp_listList.search(is)) {
			size_t numberElements;
			is >> numberElements;
			CommHoleLocalizer::BoundingBox listItem;
			std::vector<CommHoleLocalizer::BoundingBox> listList;
			kmp_list.search(is);
			for(counter=0; counter<numberElements; counter++) {
				if(kmp_list.search(is)) {
					listItem.from_xml(is);
					listList.push_back(listItem);
				}
			}
			setList(listList);
		}
	}
	
