	const char* QueryCore::getCompiledHash()
	{
		return CommHoleLocalizerBenchmarkIDL::REPO_HASH;
	}
	
	void QueryCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void QueryCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "QueryCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t QueryCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.value);
		boost::hash_combine(seed, data.minHoleSize);
		boost::hash_combine(seed, data.maxHoleSize);
		boost::hash_combine(seed, data.isMultipleHoles);
		boost::hash_combine(seed, data.minPose);
		boost::hash_combine(seed, data.maxPose);
		boost::hash_combine(seed, data.occlusion);
		boost::hash_combine(seed, data.illumination);
		
		return seed;
	}
	#endif
	
	// default constructor
	QueryCore::QueryCore()
	:	idl_Query()
	{  
		setValue(0);
		setMinHoleSize(0);
		setMaxHoleSize(0);
		setIsMultipleHoles(false);
		setMinPose(0);
		setMaxPose(0);
		setOcclusion(0);
		setIllumination(0);
	}
	
	QueryCore::QueryCore(const DATATYPE &data)
	:	idl_Query(data)
	{  }

	QueryCore::~QueryCore()
	{  }
	
	void QueryCore::to_ostream(std::ostream &os) const
	{
	  os << "Query(";
	  os << getValue() << " ";
	  os << getMinHoleSize() << " ";
	  os << getMaxHoleSize() << " ";
	  os << getIsMultipleHoles() << " ";
	  os << getMinPose() << " ";
	  os << getMaxPose() << " ";
	  os << getOcclusion() << " ";
	  os << getIllumination() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void QueryCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<value>" << getValue() << "</value>";
		os << indent << "<minHoleSize>" << getMinHoleSize() << "</minHoleSize>";
		os << indent << "<maxHoleSize>" << getMaxHoleSize() << "</maxHoleSize>";
		os << indent << "<isMultipleHoles>" << getIsMultipleHoles() << "</isMultipleHoles>";
		os << indent << "<minPose>" << getMinPose() << "</minPose>";
		os << indent << "<maxPose>" << getMaxPose() << "</maxPose>";
		os << indent << "<occlusion>" << getOcclusion() << "</occlusion>";
		os << indent << "<illumination>" << getIllumination() << "</illumination>";
	}
	
	// restore from xml stream
	void QueryCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_value("<value>");
		static const Smart::KnuthMorrisPratt kmp_minHoleSize("<minHoleSize>");
		static const Smart::KnuthMorrisPratt kmp_maxHoleSize("<maxHoleSize>");
		static const Smart::KnuthMorrisPratt kmp_isMultipleHoles("<isMultipleHoles>");
		static const Smart::KnuthMorrisPratt kmp_minPose("<minPose>");
		static const Smart::KnuthMorrisPratt kmp_maxPose("<maxPose>");
		static const Smart::KnuthMorrisPratt kmp_occlusion("<occlusion>");
		static const Smart::KnuthMorrisPratt kmp_illumination("<illumination>");
		
		if(kmp_value.search(is)) {
			short valueItem;
			is >> valueItem;
			setValue(valueItem);
		}
		if(kmp_minHoleSize.search(is)) {
			char minHoleSizeItem;
			is >> minHoleSizeItem;
			setMinHoleSize(minHoleSizeItem);
		}
		if(kmp_maxHoleSize.search(is)) {
			char maxHoleSizeItem;
			is >> maxHoleSizeItem;
			setMaxHoleSize(maxHoleSizeItem);
		}
		if(kmp_isMultipleHoles.search(is)) {
			bool isMultipleHolesItem;
			is >> isMultipleHolesItem;
			setIsMultipleHoles(isMultipleHolesItem);
		}
		if(kmp_minPose.search(is)) {
			char minPoseItem;
			is >> minPoseItem;
			setMinPose(minPoseItem);
		}
		if(kmp_maxPose.search(is)) {
			char maxPoseItem;
			is >> maxPoseItem;
			setMaxPose(maxPoseItem);
		}
		if(kmp_occlusion.search(is)) {
			char occlusionItem;
			is >> occlusionItem;
			setOcclusion(occlusionItem);
		}
		if(kmp_illumination.search(is)) {
			char illuminationItem;
			is >> illuminationItem;
			setIllumination(illuminationItem);
		}
	}
	
